const items = $input.all();

return items.map(item => {
  const input = item.json ?? {};

  const source =
    input.channel_post ??
    input.message ??
    input.contentSummary ??
    {};

  const chat = source.chat ?? input.message?.chat ?? input.channel_post?.chat ?? {};
  const senderChat = source.sender_chat ?? {};
  const channelUsername = chat.username ?? senderChat.username ?? '';

  const article = {
    telegramMessageId:
      source.message_id ??
      source.messageId ??
      input.message?.message_id ??
      input.contentSummary?.messageId ??
      null,
    channelId:
      chat.id ??
      input.message?.chat?.id ??
      input.contentSummary?.chatId ??
      null,
    channelUsername,
    channelTitle: chat.title ?? senderChat.title ?? null,
    rawText: source.text ?? source.caption ?? '',
    mediaType: null,
    mediaFileId: null,
  };

  const metadata = [];
  if (channelUsername) metadata.push(`CHANNEL_USERNAME: @${channelUsername}`);
  if (article.channelTitle) metadata.push(`CHANNEL_TITLE: ${article.channelTitle}`);

  const attachMeta = (fileId, mediaType) => {
    if (!fileId) return;
    article.mediaFileId = fileId;
    article.mediaType = mediaType;
    metadata.push(`FILE_ID: ${fileId}`);
    metadata.push(`MEDIA_TYPE: ${mediaType}`);
  };

  if (input.contentSummary?.hasMedia) {
    const mediaType = input.contentSummary.mediaType ?? 'media';
    const fileId = input.media?.fileId ?? input.media?.largestPhoto?.file_id ?? null;
    attachMeta(fileId, mediaType);
  } else if (Array.isArray(source.photo) && source.photo.length) {
    const largest = source.photo[source.photo.length - 1];
    attachMeta(largest.file_id, 'photo');
  } else if (source.video) {
    attachMeta(source.video.file_id, 'video');
  } else if (source.document) {
    attachMeta(source.document.file_id, 'document');
  } else if (source.audio) {
    attachMeta(source.audio.file_id, 'audio');
  }

  if (!article.rawText && input.contentSummary) {
    article.rawText =
      input.contentSummary.text ??
      input.contentSummary.caption ??
      '';
  }

  article.telegramUrl =
    article.channelUsername && article.telegramMessageId
      ? `https://t.me/${article.channelUsername}/${article.telegramMessageId}`
      : null;

  return { json: { article, metadata } };
});

const article = $json.article ?? {};
const embeddingValues =
  $json.embedding?.values ??
  $json.embedding ??
  [];
const metadata = $json.output ?? {};

if (!article.telegramMessageId) {
  throw new Error('article.telegramMessageId missing before Neo4j write.');
}
if (!embeddingValues.length) {
  throw new Error('embedding values missing before Neo4j write.');
}

const topics = Array.isArray(metadata.topics) ? metadata.topics : [];
const tags = Array.isArray(metadata.tags) ? metadata.tags : [];
const entities = Array.isArray(metadata.entities) ? metadata.entities : [];

const entityNames = entities.map(e => String(e.name || '')).filter(Boolean);
const entityTypes = entities.map(e => String(e.entityType || '')).filter(Boolean);

const channelUsername = article.channelUsername ?? '';
const telegramUrl =
  channelUsername && article.telegramMessageId
    ? `https://t.me/${channelUsername}/${article.telegramMessageId}`
    : null;

const statement = `
MERGE (c:Channel {id: $channel_id})
  ON CREATE SET c.username = $channel_username
SET c.title = $channel_title

MERGE (a:Article {telegram_message_id: $telegram_message_id})
SET a.channel_id = $channel_id,
    a.channel_username = $channel_username,
    a.telegram_url = coalesce($telegram_url, a.telegram_url),
    a.raw_text = $raw_text,
    a.media_type = $media_type,
    a.media_file_id = $media_file_id,
    a.embedding = $embedding,
    a.title = $title,
    a.summary = $summary,
    a.topics = $topics,
    a.tags = $tags,
    a.entity_names = $entity_names,
    a.entity_types = $entity_types,
    a.cta_text = $cta_text,
    a.cta_link = $cta_link,
    a.topic_decision_required = $topic_decision_required,
    a.suggested_topic = $suggested_topic,
    a.ingested_at = datetime()
MERGE (c)-[:PUBLISHED]->(a)
RETURN a.telegram_message_id AS telegram_message_id
`.trim();

return [
  {
    json: {
      statement,
      parameters: {
        telegram_message_id: String(article.telegramMessageId ?? ''),
        channel_id: String(article.channelId ?? ''),
        channel_title: article.channelTitle ?? null,
        channel_username: channelUsername,
        telegram_url: telegramUrl,
        raw_text: article.rawText ?? '',
        media_type: article.mediaType ?? null,
        media_file_id: article.mediaFileId ?? null,
        embedding: embeddingValues.map(Number),
        title: metadata.title ?? '',
        summary: metadata.summary ?? '',
        topics,
        tags,
        entity_names: entityNames,
        entity_types: entityTypes,
        cta_text: metadata.ctaText ?? '',
        cta_link: metadata.ctaLink ?? '',
        topic_decision_required: Boolean(metadata.topicDecisionRequired),
        suggested_topic: metadata.suggestedTopic ?? ''
      }
    }
  }
];
